BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "FeatureSpot" (
	"featureSpotID"	INTEGER,
	"stationSpotID"	INTEGER,
	"x"	NUMERIC,
	"y"	NUMERIC,
	"index"	INTEGER,
	"spotImage"	TEXT,
	"title"	TEXT,
	"detailDescription"	TEXT
);
CREATE TABLE IF NOT EXISTS "MCQuestionChoice" (
	"stationID"	INTEGER,
	"mcQuestionID"	INTEGER,
	PRIMARY KEY("stationID","mcQuestionID"),
	FOREIGN KEY("mcQuestionID") REFERENCES "MultipleChoiceQuestion"("mcQuestionID"),
	FOREIGN KEY("stationID") REFERENCES "Station"("stationID")
);
CREATE TABLE IF NOT EXISTS "MultipleChoiceQuestion" (
	"mcQuestionID"	INTEGER,
	"mcQuestion"	TEXT,
	"choice1"	TEXT,
	"choice2"	TEXT,
	"choice3"	TEXT,
	"choice4"	TEXT,
	"mcAnswer"	TEXT,
	PRIMARY KEY("mcQuestionID")
);
CREATE TABLE IF NOT EXISTS "Station" (
	"stationID"	INTEGER NOT NULL,
	"image"	TEXT,
	"finish"	INTEGER,
	PRIMARY KEY("stationID")
);
CREATE TABLE IF NOT EXISTS "StationSpot" (
	"stationSpotID"	INTEGER,
	"stationID"	INTEGER,
	"place"	TEXT,
	"description"	TEXT,
	"picture1"	TEXT,
	"picture2"	TEXT,
	"picture3"	TEXT,
	"picture4"	TEXT,
	"locationX"	NUMERIC,
	"locationY"	NUMERIC,
	PRIMARY KEY("stationSpotID"),
	FOREIGN KEY("stationID") REFERENCES "Station"("stationID")
);
CREATE TABLE IF NOT EXISTS "TFQuestionChoice" (
	"stationID"	INTEGER,
	"tfQuestionID"	INTEGER,
	PRIMARY KEY("stationID","tfQuestionID"),
	FOREIGN KEY("stationID") REFERENCES "Station"("stationID"),
	FOREIGN KEY("tfQuestionID") REFERENCES "TFQuestionChoice"("tfQuestionID")
);
CREATE TABLE IF NOT EXISTS "TrueFalseQuestion" (
	"tfQuestionID"	INTEGER,
	"tfQuestion"	TEXT,
	"tfAnswer"	TEXT,
	PRIMARY KEY("tfQuestionID")
);
CREATE TABLE IF NOT EXISTS "stations (original)" (
	"stationId"	INTEGER,
	"stationImage"	TEXT,
	"stationName"	TEXT,
	"stationInfo"	TEXT,
	"stationDes1"	TEXT,
	"stationDes2"	TEXT,
	"stationDes3"	TEXT,
	"stationDes1Title"	TEXT,
	"stationDes2Title"	TEXT,
	"stationDes3Title"	TEXT,
	"stationLatitude"	REAL,
	"stationLongtitude"	REAL,
	PRIMARY KEY("stationId")
);
COMMIT;
