INSERT INTO "StationSpot" VALUES (1,1,'金城茶園','黃金城老師的種茶動機，起源於自己喝到很好喝的茶，就將家中的竹筍園改成茶園，取名「金城茶園」，堅持友善無毒栽種金萱、青心烏龍等。','original_14_1','original_14_2','original_14_3',NULL,24.98827,121.618095);
INSERT INTO "StationSpot" VALUES (2,2,'大崎嶺步道口','大崎嶺步道入口位在阿柔洋產業道路約250公尺，步道入口在道路右側槡園旁的一條小徑。昔日為聯繫聚落的重要山徑。步道沿途林木蒼鬱，景色優美，並保留許多歷史遺跡，如石階與古碑，展現先民開拓的足跡。','original_15','original_15_1','original_15_3',NULL,24.996291,121.6136);
INSERT INTO "StationSpot" VALUES (3,4,'石媽祖古道','石媽祖步道入口在阿柔洋產業道路約450公尺處，步道終點為鎮南宮石媽祖廟，沿路兩側桂花樹撲鼻，風景優美，適合親子健行。','original_12','original_12_1','original_12_2',NULL,24.993897,121.61471);
INSERT INTO "StationSpot" VALUES (4,5,'阿柔坑溪親水空間','阿柔坑溪隱身於青翠山林間，溪水沿層層石階流瀉，形成如畫般的階梯水景。溪畔綠意盎然，並設有涼亭供遊客休憩，環境幽靜清新。漫步於潺潺水聲中，遠離城市喧囂，感受大自然的寧靜與療癒氣息，是放鬆身心的理想去處。','original_11_1','original_11_2','original_11_3','original_11_4',24.993184,121.617922);
INSERT INTO "StationSpot" VALUES (5,6,'向天湖','位於阿柔洋產業道路東側，仍保有原始田園風貌，種植水稻、蔬菜與綠竹筍，四季更迭展現農村之美。每年四月，螢火蟲飛舞，螢光閃閃。房舍後方有大片桂竹林，環境純樸自然。插秧與收割時，鄰里相互幫忙，充滿濃厚人情味。','original_5','original_1','original_3','original_4',24.992187,121.619625);
INSERT INTO "StationSpot" VALUES (6,8,'加爾默羅聖母聖衣隱修院','位在寧靜的深山中的加爾默羅聖母聖衣隱修院，為天主教最嚴格的隱修院之一。在靜默中祈祷，隱修女度著靜默生活，終身奉獻，祈禱刻苦、也透過彼此相互祈禱，使她們之間的生命更加緊密地連結在一起。','original_3_1','original_3',NULL,NULL,24.998224,121.612849);
INSERT INTO "StationSpot" VALUES (7,9,'1792茶園','1792茶園創辦人黃土水深刻體會到深坑茶產業面臨的挑戰，決心以實際行動振興在地茶業。2024年新北好茶石碇冬季文山包種茶比賽，榮獲特等獎！為振興深坑茶產業奠定了關鍵基石。','original_2','original_2_1',NULL,NULL,24.98827,121.618095);
INSERT INTO "StationSpot" VALUES (8,10,'驪山老母-無極慈母宮','創建於民國九十年，主祀驪山老母，奉祀太上道祖。驪山老母為道教女仙，常指點迷津、傳授秘籍，深受信眾崇敬，民間影響深遠。蔡貴德宮主因夢中多次感應，依神示雕塑聖像，並擇吉日開光奉祀。','original_1','original_1_2','original_1_3',NULL,24.984065,121.614538);
INSERT INTO "StationSpot" VALUES (9,12,'阿柔洋產業道路','阿柔洋產業道路四季皆有不同風貌，春櫻綻放、夏日翠綠、秋季迷人、冬晨雲霧繚繞，變化萬千，景色迷人。這條道路是單車愛好者必訪之地，以其綿延的緩升坡聞名，挑戰體力之餘，也能欣賞沿途壯闊山景與純樸田野風光。','original_6','aipicture_6',NULL,NULL,37.422015,-122.084063);
INSERT INTO "StationSpot" VALUES (10,13,'青山香草教育農園','阿柔地產的桑麻丸與在地花藝，採用在地生產的芝麻、蜂蜜和桑葉製作而成，展現深坑滿滿的農藝魅力與文化底蘊。','original_8_1','original_8_2','original_8','original_8_4',24.975459,121.614972);
INSERT INTO "StationSpot" VALUES (11,15,'天南宮','敬奉的三太子元祖已有三百多年歷史，於清嘉慶年間自安溪縣遷祀來台，日據時期供奉於文山郡深坑庄大崙尾一號。每年農曆九月九日為三太子聖誕，信眾齊聚慶典，共同祈福，延續傳統信仰。','original_16','original_16_1',NULL,NULL,24.97521,121.615131);
INSERT INTO "StationSpot" VALUES (12,16,'106 Club House','阿柔茶文化步道的起點，位於台106乙線的鐵馬驛站，距離台北101開車只要10分鐘。鄰近的阿柔楊產業道路則是自行車好手練車的地方。','original_18_2',NULL,NULL,NULL,24.998224,121.612849);
INSERT INTO "StationSpot" VALUES (13,17,'新北台北縣市交會處：深坑與木柵','此交會處為行政區交界，一邊為新北市深坑區、另一邊為台北市文山區。','original_20','original_20_1',NULL,NULL,24.973378,121.614516);
